EAST = ">"
SOUTH = "v"


def solve(puzzle_input):
    global EAST, SOUTH
    grid = [[x for x in y] for y in puzzle_input.strip().split("\n")]

    steps = 0
    while True:
        changes = 0
        for herd in [EAST, SOUTH]:
            new_grid = [["."] * len(grid[0]) for x in range(0, len(grid))]
            for y, row in enumerate(grid):
                for x, cell in enumerate(row):
                    if new_grid[y][x] != ".":
                        continue
                    if cell == herd:
                        if herd == EAST:
                            # check right
                            if x < len(row) - 1:
                                next_x = x + 1
                            else:
                                next_x = 0
                            if row[next_x] == ".":
                                new_grid[y][next_x] = EAST
                                changes += 1
                            else:
                                new_grid[y][x] = EAST
                        elif herd == SOUTH:
                            # check below
                            if y < len(grid) - 1:
                                next_y = y + 1
                            else:
                                next_y = 0
                            if grid[next_y][x] == ".":
                                new_grid[next_y][x] = SOUTH
                                changes += 1
                            else:
                                new_grid[y][x] = SOUTH
                    else:
                        new_grid[y][x] = cell
            grid = new_grid
        steps += 1
        if changes == 0:
            break

    print(steps)

    return


